import React from 'react';
import axios from "axios";

class ListFavoris extends React.Component {
    constructor(props){
        super(props); //login
        this.state ={movies: [], series: []};

    }

    componentDidMount(){
        // Recupere les Favoris de l'utilisateur 
        const url = new URLSearchParams();
        url.append('login', this.props.login);
        axios.get('http://localhost:8080/MochiCine/Favoris/List?'+url).then(response => this.lister(response));
    }

    lister(rep){
        if(rep.data["code"]){
            this.setState ({statut: "error", textError: rep.data["message"]});
            window.confirm(this.state.textError);
        }else{
            this.setState({series: rep.data["data"], movies: rep.data["movie"]});
        } 
    }

    getSerieById(id){
        alert("Ici on affiche la description OU on renvoie vers une page html");
    }

    formatDate(string){
        var options = { year: 'numeric', month: 'long', day: 'numeric' };
        return new Date(string).toLocaleDateString([],options);
    }

    getBox(item){
        return(
            <div className="col-md-12 " key={item.id}>
            <div className="blog-entry  col-12">
                <div id = "leftbox">
                    <img src={"https://image.tmdb.org/t/p/w500/"+item.backdrop_path} alt={"pic_of_"+item.title} width="100%"  />
                </div>
                <div id = "rightbox">
                    <div className="text text-2 text-center pl-md-4">
                        <h3 className="mb-2"  onClick={() => this.getSerieById(item.id)} >{item.name}</h3>
                    </div>
                    <div className="meta-wrap">
                        <p className="meta">
                            <span><i className="far fa-calendar-alt mr-2"></i>{this.formatDate(item.first_air_date)}</span>
                            &nbsp;&nbsp;&nbsp;&nbsp;
                            <span><i className="fas fa-fire-alt mr-2"></i>{item.popularity}</span>
                            &nbsp;&nbsp;&nbsp;&nbsp;
                            <span><i className="fas fa-tv mr-2"></i>{item.number_of_seasons} saisons</span>
                        </p>
                    </div>
                    <p className="mb-4">
                        {item.overview}
                    </p>
                    <p>
                    <button className="btn btn-sm btn-outline-secondary" type="button" onClick={() => alert("Delete to favoris")}>Delete</button>
                    </p>
                </div>
            </div>
        </div>
        );
    }


    render(){
        let series;
        let movies;


        if(this.state.series.length > 0){
            series = this.state.series.map( item => {
                if(item.backdrop_path!==null && item.backdrop_path!==undefined){
                    return ( this.getBox(item) );
                }
            });
        }

        if(this.state.movies.length > 0){
            movies = this.state.movies.map( item => {
                if(item.backdrop_path!==null && item.backdrop_path!==undefined){
                    return (this.getBox(item));
                }
            });
        }

        return(
            <div className="ListFavoris col">
                <div className="row pt-md-3">
                    {/*
                    <div className="col-md-12 ">
                        <div className="blog-entry col-12 bg-secondary">
                                <a>EXEMPLE</a>
                                <div className="text text-2 pl-md-4">
                                    <h3 className="mb-2">TITLE OF MOVIE</h3>
                                </div>
                                <div className="meta-wrap">
                                    <p className="meta">
                                        <span>Dec 14, 2018</span>
                                    </p>
                                </div>
                                <p className="mb-4">
                                    A small river named Duden flows by their place and supplies it with the necessary regelialia.
                                </p>
                                <p>
                                    <button>delete</button>
                                </p>
                        </div>
                    </div>*/}

                    {series}
                    {movies}

                </div>
            </div>
        );
    }



}

export default ListFavoris;
