import React, { Component } from 'react';
import './Calendrier.css';
import axios from 'axios';

class Calendrier extends Component{

    constructor(props){
        super(props);
        this.state={lundiListe : [], mardiListe: []};
        this.constr=this.constr.bind(this);
        this.getSerieById=this.getSerieById.bind(this);
    }

    componentDidMount(){

        // Today's date
        let today= new Date();
        today.setDate(today.getDate()-2);
        today.setHours(0,0,0,0);

        //console.log(today);
        //Today release
        axios.get("https://api.themoviedb.org/3/tv/airing_today?api_key=a3be1be132d237a0716cc27bdae1b2f0&language=en-US&page=1").then(res=> this.constr(res));

        //Future release
        //let mo=[5];
        axios.get("https://api.themoviedb.org/3/tv/on_the_air?api_key=a3be1be132d237a0716cc27bdae1b2f0&language=en-US&page=1").then(res=> res.data.results).then(
            ex => {

                //Find the serie to find the date of the last episode
                ex.map( key =>
                    axios.get("https://api.themoviedb.org/3/tv/"+key.id+"?api_key=a3be1be132d237a0716cc27bdae1b2f0&language=en-US").then(
                    v => {

                    let tvserie=v.data;
                    let exdate= new Date(tvserie.last_air_date);
                    exdate.setHours(0,0,0,0);
                    console.log(exdate);
                    //console.log(today.getTime()+"=="+exdate.getTime());
                    if (today.getTime()===exdate.getTime()) 
                        {   
                            //Black Magic
                            this.setState({lundiListe: [...this.state.lundiListe, tvserie]});
                        } 
                    })
                )

                });
        //console.log(this.state.lundiListe);
        //this.setState({lundiListe: mo});
	}

    constr(res){
        const listeoftv=res.data.results;
        this.setState({liste : listeoftv});
        //console.log(this.state.liste);
    }

    getSerieById(id){
        alert("Ici on affiche la description OU on renvoie vers une page html");
    }



    render(){

        //console.log(this.state.lundiListe);
        let semaine=["Dimanche","Lundi","Mardi","Mercredi","Jeudi","Vendredi","Samedi"];
        let jour= new Date().getDay();
        //let jour=new Date(ex.last_air_date).getDay();
        //On affiche les sÃ©ries
        let tmp = this.state.lundiListe.map(ex => {
            //console.log(ex);
            let semaine=["Dimanche","Lundi","Mardi","Mercredi","Jeudi","Vendredi","Samedi"];
            let jour=new Date(ex.last_air_date).getDay();
            //console.log(semaine[jour]);
                return(

                    <div className="test"> 
                        <img src={"https://image.tmdb.org/t/p/w500/"+ex.backdrop_path} alt={"pic_of_"+ex.name} width="130" height="150"/>
                        <div>
                            <button id= "name" onClick={() => this.getSerieById(ex.id)} >{ex.name}</button>
                        </div>
                    </div>

                )

        })



        //console.log(tmp);
        //axios.get("https://api.themoviedb.org/3/tv/airing_today?api_key=a3be1be132d237a0716cc27bdae1b2f0&language=en-US&page=1").then(res=> this.constr(res));
        return (
            <div className="Rectangle">
                <div className="Jours">
                    <strong> {semaine[jour%7]} </strong>
                </div>
               {tmp}
                <div className="Jours">
                    <strong> {semaine[(jour+1)%7]} </strong>
                </div>
                <div className="Jours">
                    <strong> {semaine[(jour+2)%7]} </strong>
                </div>
                <div className="Jours">
                    <strong> {semaine[(jour+3)%7]} </strong>
                </div>
                <div className="Jours">
                    <strong> {semaine[(jour+4)%7]} </strong>
                </div>
                <div className="Jours">
                    <strong> {semaine[(jour+5)%7]} </strong>
                </div>
                <div className="Jours">
                    <strong> {semaine[(jour+6)%7]} </strong>
                </div>
            </div>
        )
    }
}

export default Calendrier;
