                            <button id= "name" onClick={() => this.getSerieById(ex.id)} >{ex.title}</button>
                        </div>
                        <div className="stars">
                            <StarRatings rating={note} starRatedColor="yellow" numberOfStars={5} name='rating' starDimension="20px"starSpacing="1px">
