import React, { Component } from 'react';
import './Calendrier.css';
import axios from 'axios';

class Calendrier extends Component{

    constructor(props){
        super(props);
        this.state={lundiListe : [], mardiListe: []};
        this.constr=this.constr.bind(this);
    }

    componentDidMount(){

        // Today's date
        let today= new Date();
        today.setDate(today.getDate()-2);
        today.setHours(0,0,0,0);

        //console.log(today);
        //Today release
        axios.get("https://api.themoviedb.org/3/tv/airing_today?api_key=a3be1be132d237a0716cc27bdae1b2f0&language=en-US&page=1").then(res=> this.constr(res));

        //Future release

        //console.log(this.state.lundiListe);
        //this.setState({lundiListe: mo});
	}

    constr(res){
        const listeoftv=res.data.results;
        this.setState({liste : listeoftv});
        //console.log(this.state.liste);
    }



    render(){

        //console.log(this.state.lundiListe);

        let tmp = this.state.lundiListe.map(ex => {
            console.log("hello");

            return(
                <div className="test"> 
                    <img src={"https://image.tmdb.org/t/p/w500/"+ex.backdrop_path} alt={"pic_of_"+ex.name} width="130" height="150"/>
                    <div>
                        <button id= "name" onClick={() => this.getSerieById(ex.id)} >{ex.name}</button>
                    </div>
                </div>
            )

        })


        //console.log(tmp);
        //axios.get("https://api.themoviedb.org/3/tv/airing_today?api_key=a3be1be132d237a0716cc27bdae1b2f0&language=en-US&page=1").then(res=> this.constr(res));
        return (
            <div className="Rectangle">
                <div className="Jours">
                    <strong> Lundi </strong>
                </div>

                <div className="Jours">
                    <strong> Mardi </strong>
                </div>
                <div className="Jours">
                    <strong> Mercredi </strong>
                </div>
                <div className="Jours">
                    <strong> Jeudi </strong>
                </div>
                <div className="Jours">
                    <strong> Vendredi </strong>
                </div>
                <div className="Jours">
                    <strong> Samedi </strong>
                </div>
                <div className="Jours">
                    <strong> Dimanche </strong>
                </div>
            </div>
        )
    }
}

export default Calendrier;
