import React, { Component } from 'react';
import './bootstrap/dist/css/bootstrap.css';
import './NavBar.css';
import Logo from "./onepiece.png";

class NavBar extends Component{

    constructor(props){
        super(props);
        this.state ={connexion:true}
    }

    Connected() {
        if(this.state.connexion)
            this.setState({ connexion : false });
        else
            this.setState({ connexion : true });
    }

    render(){
        let button;
        console.log("La connexion : "+this.connexion );
        if (this.state.connexion){
            button = <button className="" onClick={this.Connected}>login</button>
        }
        else{
            button = <button className="" onClick={this.Connected}>logout</button>
        }

        return (
            <div className="NavBar">
                <nav className="navbar navbar-dark bg-dark">
                   {/* Pour la recherche */}
                    <div>
                        <div className="logodiv">
                            <img className="logosite" src={Logo} alt="logo of site"/>
                        </div>
                        <div className="Search">
                            <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search"/>
                        </div>
                        <div  className="searchButton">
                            <button className="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
                        </div>
                        <div className="connexion ml-1">
                            {button}
                        </div>

                    </div>
                </nav>

            </div>   
        )
    }
}

export default NavBar;
