import React, { Component } from 'react';
import './Calendrier.css';
import axios from 'axios';

class Calendrier extends Component{

    constructor(props){
        super(props);
        this.state={liste : 'empty'};
        this.constr=this.constr.bind(this);
    }

    componentDidMount(){
		axios.get("https://api.themoviedb.org/3/tv/airing_today?api_key=a3be1be132d237a0716cc27bdae1b2f0&language=en-US&page=1").then(res=> this.constr(res));
	}

    constr(res){
        console.log(res.data.results);
        const listeoftv=res.data.results;
        this.setState({liste : listeoftv});
        console.log(this.state.liste);
    }


    render(){

        //axios.get("https://api.themoviedb.org/3/tv/airing_today?api_key=a3be1be132d237a0716cc27bdae1b2f0&language=en-US&page=1").then(res=> this.constr(res));
        return (
            <div className="Rectangle">
                <div className="Jours">
                    <strong> Lundi </strong>

                </div>
                <div className="Jours">
                    <strong> Mardi </strong>
                </div>
                <div className="Jours">
                    <strong> Mercredi </strong>
                </div>
                <div className="Jours">
                    <strong> Jeudi </strong>
                </div>
                <div className="Jours">
                    <strong> Vendredi </strong>
                </div>
                <div className="Jours">
                    <strong> Samedi </strong>
                </div>
                <div className="Jours">
                    <strong> Dimanche </strong>
                </div>
            </div>
        )
    }
}

export default Calendrier;
