import React, { Component } from 'react';
import './Calendrier.css';
import axios from 'axios';

class Calendrier extends Component{

    constructor(props){
        super(props);
        this.state={liste : []};
        this.constr=this.constr.bind(this);
        this.getSerieById=this.getSerieById.bind(this);
    }

    componentDidMount(){
		axios.get("https://api.themoviedb.org/3/tv/airing_today?api_key=a3be1be132d237a0716cc27bdae1b2f0&language=en-US&page=1").then(res=> this.constr(res));
	}

    constr(res){
        const listeoftv=res.data.results;
        this.setState({liste : listeoftv});
        //console.log(this.state.liste);
    }

    getSerieById(id){
        axios.get("https://api.themoviedb.org/3/tv/"+id+"?api_key=a3be1be132d237a0716cc27bdae1b2f0&language=en-US").then(res=> alert("Informations sur la Serie"));
    }


    render(){
        let tmp = this.state.liste.map(ex => {
            return(
                <div className="test"> 
                    <img src={"https://image.tmdb.org/t/p/w500/"+ex.backdrop_path} width="130" height="150"/>
                    <div>
                        <button id= "name" onClick={() => this.getSerieById(ex.id)} >{ex.name}</button>
                    </div>
                </div>
            )
        })

        //axios.get("https://api.themoviedb.org/3/tv/airing_today?api_key=a3be1be132d237a0716cc27bdae1b2f0&language=en-US&page=1").then(res=> this.constr(res));
        return (
            <div className="Rectangle">
                <div className="Jours">
                    <strong> Lundi </strong>
                </div>
                {tmp}
                <div className="Jours">
                    <strong> Mardi </strong>
                </div>
                <div className="Jours">
                    <strong> Mercredi </strong>
                </div>
                <div className="Jours">
                    <strong> Jeudi </strong>
                </div>
                <div className="Jours">
                    <strong> Vendredi </strong>
                </div>
                <div className="Jours">
                    <strong> Samedi </strong>
                </div>
                <div className="Jours">
                    <strong> Dimanche </strong>
                </div>
            </div>
        )
    }
}

export default Calendrier;
