import React, { Component } from 'react';
import './Calendrier.css';
import axios from 'axios';

class Calendrier extends Component{

    constructor(props){
        super(props);
        this.state={liste : [], mardiListe: []};
        this.constr=this.constr.bind(this);
    }

    componentDidMount(){
        //Today release
        axios.get("https://api.themoviedb.org/3/tv/airing_today?api_key=a3be1be132d237a0716cc27bdae1b2f0&language=en-US&page=1").then(res=> this.constr(res));

        //Future release
        axios.get("https://api.themoviedb.org/3/tv/on_the_air?api_key=a3be1be132d237a0716cc27bdae1b2f0&language=en-US&page=1").then(res=> this.constrMardi(res));
	}

    constr(res){
        const listeoftv=res.data.results;
        this.setState({liste : listeoftv});
        //console.log(this.state.liste);
    }

    constrMardi(res){
        let today=new Date().getDate();
        console.log(today);
        const listeoftv=res.data.results;
        let mo =Array();
        let mardi= listeoftv.map(ex => {
            let tmp= new Array(5);
            let promise= axios.get("https://api.themoviedb.org/3/tv/"+ex.id+"?api_key=a3be1be132d237a0716cc27bdae1b2f0&language=en-US").then(res=> {return res});
            //promise.then(v => {console.log(new Date(v.data.last_air_date).getDate()) });
            //promise.then(v => {if ((new Date(v.data.last_air_date).getDay())==2) {mo.push(v.data)} });
        })
        //console.log(mo);
        //this.setState({mardiListe : listeoftv});
        //console.log(this.state.mardiListe);
    }

    render(){

        let tmp = this.state.liste.map(ex => {
            return(
                <div className="test"> 
                    <img src={"https://image.tmdb.org/t/p/w500/"+ex.backdrop_path} alt={"pic_of_"+ex.name} width="130" height="150"/>
                    <div>
                        <button id= "name" onClick={() => this.getSerieById(ex.id)} >{ex.name}</button>
                    </div>
                </div>
            )
        })

        //axios.get("https://api.themoviedb.org/3/tv/airing_today?api_key=a3be1be132d237a0716cc27bdae1b2f0&language=en-US&page=1").then(res=> this.constr(res));
        return (
            <div className="Rectangle">
                <div className="Jours">
                    <strong> Lundi </strong>
                </div>
                {tmp}
                <div className="Jours">
                    <strong> Mardi </strong>
                </div>
                <div className="Jours">
                    <strong> Mercredi </strong>
                </div>
                <div className="Jours">
                    <strong> Jeudi </strong>
                </div>
                <div className="Jours">
                    <strong> Vendredi </strong>
                </div>
                <div className="Jours">
                    <strong> Samedi </strong>
                </div>
                <div className="Jours">
                    <strong> Dimanche </strong>
                </div>
            </div>
        )
    }
}

export default Calendrier;
